mapper.insert:
insert into movie_like values( #{mov_idn}, #{user_id} )
select mov_idn, number, now() from movie_mst mm LEFT OUTER JOIN watch_list wl ON(wl.mov_idn = mm.mov_idn AND wl.number = #{number}) WHERE mm.mov_idn = #{mov_idn};
)

mapper.update:

update
	movie_rating
set
	rating = #{rating}
where
	mov_idn = #{mov_idn} AND user_id = #{user_id};
